import React from "react";
import styled from "@emotion/styled";

import { Form, Input, Checkbox, Button } from "antd";

const StyledRegister = styled.div`
  width: 600px;
  height: 1200px;
  left: 50%;
  top: 50%;
  margin: auto;
  margin-top: 200px;
  display: flex;
  flex-direction: column;
  > .Register {
    text-align: center;
    display: flex;
    flex-direction: column;
    span:nth-of-type(1) {
      text-align: center;
      color: #767676;
      font: normal normal 900 40px/59px Noto Sans CJK KR;
    }
    span:nth-of-type(2) {
      color: #bbbbbb;
      font: normal normal normal 18px/27px Noto Sans CJK KR;
      padding: 10px 0px;
      margin-bottom: 30px;
    }
  }
  form {
    span {
      text-align: center;
      color: #767676;
      font-display: normal normal 900 40px/59px Noto Sans CJK KR;
    }
    > div {
      color: #767676;
      font: normal normal normal 22px/33px Noto Sans CJK KR;
      padding: 30px 0px;
      display: flex;
      justify-content: space-between;
      label {
      }
    }
  }
`;

const layout = {
  labelCol: { span: 0 },
  wrapperCol: { span: 16 },
};

const tailLayout = {
  wrapperCol: { offset: 8, span: 16 },
};

const Demo = () => {
  const onFinish = (values) => {
    console.log("Success:", values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };

  return (
    <Form {...layout} name="basic" initialValues={{ remember: true }} onFinish={onFinish} onFinishFailed={onFinishFailed}>
      <Form.Item label="Username" name="username" rules={[{ required: true, message: "Please input your username!" }]}>
        <Input />
      </Form.Item>

      <Form.Item label="Password" name="password" rules={[{ required: true, message: "Please input your password!" }]}>
        <Input.Password />
      </Form.Item>

      <Form.Item {...tailLayout} name="remember" valuePropName="checked">
        <Checkbox>Remember me</Checkbox>
      </Form.Item>

      <Form.Item {...tailLayout}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
  );
};
const CustomInput = ({ label, name, message, notreq }) => {
  return (
    <Form.Item label={label} name={name} rules={[{ required: notreq ? false : true, message: message ? message : null }]}>
      <Input
        style={{
          border: "none",
          borderBottom: "1px solid #767676",
        }}
      />
    </Form.Item>
  );
};

const Register = () => (
  <StyledRegister>
    <div className="Register">
      <span>회원 가입</span>
      <span>* 표시는 필수 입력 항목입니다.</span>
    </div>
    <Form {...layout} name="basic" initialValues={{ remember: true }}>
      <Form.Item label="아이디" name="id" rules={[{ required: true, message: "아이디를 입력해주세요!" }]}>
        <Input
          style={{
            border: "none",
            borderBottom: "1px solid #767676",
          }}
        />
      </Form.Item>
      <button
        style={{
          background: "#BBBBBB",
          border: "none",
          borderRadius: "17px",
          width: "92px",
          height: "34px",
        }}
      >
        <span style={{ color: "#FFFFFF" }}>중복확인</span>
      </button>
      <CustomInput label="* 비밀번호" id="password" message="비밀번호를 입력해주세요!" />
      <CustomInput label="* 비밀번호확인" id="passwordCheck" message="비밀번호 확인을 입력해주세요!" />
      <CustomInput label="* 이메일" id="email" message="이메일을 입력해주세요!" />
      <CustomInput label="* 휴대폰번호" id="phonenumber" message="휴대폰번호를 입력해주세요!" />
      <CustomInput label="* 전화번호" notreq={true} id="password" />
    </Form>
    <div
      style={{
        display: "flex",
        justifyContent: "space-between",
        marginTop: "30px",
      }}
    >
      <button
        style={{
          width: "280px",
          height: "75px",
          backgroundColor: "#BBBBBB",
          border: "none",
          borderRadius: "10px",
        }}
      >
        <span
          style={{
            color: "#FFFFFF",
            font: "normal normal bold 22px/33px Noto Sans CJK KR",
          }}
        >
          취소하기
        </span>
      </button>
      <button
        style={{
          width: "280px",
          height: " 75px",
          backgroundColor: "#767676",
          border: "none",
          borderRadius: "10px",
        }}
      >
        <span
          style={{
            color: "#FFFFFF",
            font: "normal normal bold 22px/33px Noto Sans CJK KR",
          }}
        >
          가입하기
        </span>
      </button>
    </div>
  </StyledRegister>
);

export default Register;
